import React, { useState, useEffect} from 'react';
import { BrowserRouter } from 'react-router-dom'
import InlineToaster from '../components/InlineToaster/styles'
import { IntlProvider } from 'react-intl';
import { DEFAULT_LOCALE } from '../i18n';
import { action } from '@storybook/addon-actions';
// import { RecipientViewContext } from '../components/RecipientList/RecipientView/index';

export default {
  title: 'Inline Toaster',
  component: 'InlineToaster'
}

export function Toaster({
  intl,
  className,
  message,
  handleUndo,
  variant,
  timer = 4500,
  clearToaster,
  showUndo = false,
  ...other
}) {

  const [show, setShow] = useState(true);

  const toasterDefault = state => ({ ...state, show: false });

  const timeout = setTimeout(() => {
    setShow(false);
    clearToaster(toasterDefault);
  }, timer);

  useEffect(() => () => {
    // Prevents memory leak
    setShow(false);
    clearTimeout(timeout);
    clearToaster(toasterDefault);
  });

  return (
    <BrowserRouter>
      <div>
        <h3>Inline Toaster</h3>
          {show &
            <InlineToaster
              className="success-toaster"
              variant="success"
              message="testing"
              clearToaster={() => {action("Timed out")}}
              variant="success"
              timer={10}
              message="test"
            />
          }
      </div>
    </BrowserRouter>
  );
}

