import React, { useContext } from 'react';
import { BrowserRouter } from 'react-router-dom'
import InlineToaster from '../components/InlineToaster/styles'
import { IntlProvider } from 'react-intl';
import { DEFAULT_LOCALE } from '../i18n';
import { RecipientViewContext } from '../components/RecipientList/RecipientView/index';

export default {
  title: 'Inline Toaster',
  component: 'InlineToaster'
}

export function Toaster(){

  const showToaster =
    toaster.show &&
    toaster.field === field &&
    toaster.recipientUuid === recipientUuid;

    const {
      toasterProps: { toaster, setToaster }
    } = useContext(RecipientViewContext);

  return (
    <BrowserRouter>
      <div>
        <h3>Inline Toaster</h3>
        <IntlProvider locale={DEFAULT_LOCALE}>
          {showToaster && (
            <InlineToaster
              variant="success"
              className="success-toaster"
              message="Testing"
              showUndo
              clearToaster={setToaster}
            />
          )}
        </IntlProvider>
      </div>
    </BrowserRouter>
  );
}
