import React, { Component } from 'react';
import { action } from '@storybook/addon-actions';
import { BrowserRouter } from 'react-router-dom'
import PropTypes from 'prop-types';
import NSButton from '../components/NSButton/index'
import { button, Typography, Collapse, IconButton, SnackbarContent } from '@material-ui/core';
import { Edit, CloseIcon, MoreHoriz, CheckCircleIcon, ErrorIcon, InfoIcon, WarningIcon, ExpandLess, ExpandMore } from '@material-ui/icons';
import { NSButtonStyles, btnLinkStyles } from '../components/NSButton/styles'
import { createMuiTheme, makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
// import { FormattedMessage } from 'react-intl';
// import TextLink from '../components/TextLink';
// import globalMessages from 'messages';



export default {
  title: 'Buttons',
  component: NSButton, button
};

export function ButtonContainer({
  children,
  componentClasses,
  version,
  textBtn,
  deleteLink,
  formId,
  disabled,
  variant,
  className,
  showUndo = false,
  ...rest
}) {
  const classes = NSButtonStyles();
  const danger = version === 3 ? classes.negative : classes.primary;
  const type = version === 2 ? classes.secondary : danger;
  const variantIcon = {
    success: CheckCircleIcon,
    warning: WarningIcon,
    error: ErrorIcon,
    info: InfoIcon,
  };
  const Icon = variantIcon[variant];

  return (
    <BrowserRouter>
    <h3>Material-UI Buttons</h3>
      <p>
        <button 
          className={classes.cancelBtn} 
          onClick={action("I've been clicked")}>
          Cancel
        </button>
      &nbsp;
        <button className="MuiButton-contained" onClick={action("I've been clicked!")}>
          <span>MUI Btn</span>
        </button>
      &nbsp;
        <button 
          className="MuiButtonBase-root MuiButton-root MuiButton-contained makeStyles-button-1807 makeStyles-bottomMargin-1809 makeStyles-topMargin-1810 MuiButton-containedPrimary MuiButton-containedSizeSmall MuiButton-sizeSmall" 
          onClick={action("I've been clicked")}>
            <span class="MuiButton-label">MUI Btn</span>
        </button>  
      </p>
    <h3>NS Base Buttons</h3>
      <p>
        <NSButton
          id="Base"
          version={1}
          componentClasses={clsx(classes.btns, classes.margin)}
          onClick={action("I've been clicked")}>
          <span>NS Btn V1</span>
        </NSButton>
      &nbsp;
        <NSButton
          id="Forward"
          version={2}
          componentClasses={clsx(classes.btns, classes.margin)}
          onClick={action("I've been clicked")}>
          <span>NS Btn V2</span>
        </NSButton>
      &nbsp;
        <NSButton
          id="Danger"
          version={3}
          componentClasses={clsx(classes.btns, classes.margin)}
          onClick={action("I've been clicked")}>
          <span>NS BTN V3</span>
        </NSButton>
      &nbsp;
        <NSButton                 
          componentClasses={classes.tsReorder}
          variant="disabled">
          <span>Disabled</span>
        </NSButton>
      &nbsp;
      <NSButton
          textBtn
          variant="contained"
          size="small"
          onClick={action("I've been clicked")}
          componentClasses={classes.tsDragNDropCancelBtn}>
        <span>Text Btn</span>
        </NSButton>
      &nbsp;
    </p>
    <h3>Buttons with Icons</h3>
      <p>
      <NSButton 
        version={2}
        onClick={action("I've been clicked")}>
        <>
          <Edit fontSize="small" className={classes.editBtn} />
          <Typography className={classes.editText}/>
          &nbsp; Edit
        </>
      </NSButton>
    &nbsp;
      <IconButton
        size="medium"
        variant="contained"
        color="primary"
        onClick={action("I've been clicked")}
        >
          <MoreHoriz className={classes.horiz} />
      </IconButton>
    &nbsp;

    </p>
    </BrowserRouter>
  );
}
