import React, { Component } from 'react';
import { action } from '@storybook/addon-actions';
import { BrowserRouter } from 'react-router-dom'
import PropTypes from 'prop-types';
import ActionPanel from '../components/ActionPanel/index'
import { actionPanelStyles } from '../components/ActionPanel/styles';



export default {
  title: 'ActionPanel',
  component: ActionPanel,
};

export function ActionPanelContainer({
  formId,
  type,
  resetForm,
  disabled,
  loading,
}) {

  const classes = actionPanelStyles();
  const [loadingState, setloadingState] = useState(false);
  const [messageState, setmessageState] = useState({
    message: '',
    open: false,
    type: 'success',
  });

  const loadingAction = (message, loading) => {
    const timeOut = setTimeout(() => {
      message(state => ({
        ...state,
        open: false,
        message: 'loading',
      }));
    }, 3000);
    loading(false);
    return timeOut;
  };

  return (
    <BrowserRouter>
    <h3>Action Panel Disabled</h3>
      <ActionPanel
            action="update"
            type=""
            formId=""
            resetForm={action("I've been clicked")}
            loading={loadingState}
            disabled={lloadingState}
      />
    <h3>Action Panel Enabled</h3>

    </BrowserRouter>
  );
}
