import React from 'react'
import NSFilters from '../components/NSFilters'
import { defineMessages, IntlProvider } from 'react-intl';
import { NSFiltersProvider } from '../components/NSFilters/context/filters';
import Wrapper from '../components/NSFilters/index';

export default {
  title: 'NSFilters',
  component: NSFilters
}

const defaultState = { query: null, chips: [] };

export const columns = [
  {
    id: 'recipientName',
    defaultWidth: '380px',
    label: 'Family',
    align: 'left',
    type: 'single',
    columnData: null,
    noData: false,
    disablePadding: true,
    show: true,
    sortable: true,
    defaultFilter: false,
    filter: false,
    custom: false,
  },
  {
    id: 'id',
    defaultWidth: '450px',
    label: 'Family ID',
    align: 'left',
    type: 'single',
    columnData: null,
    noData: false,
    disablePadding: false,
    show: true,
    sortable: true,
    filter: false,
    defaultFilter: false,
    custom: false,
  },
  {
    id: 'completedSurveys',
    defaultWidth: '123px',
    type: 'multi',
    label: 'Completed Surveys',
    filterWidth: 170,
    align: 'center',
    columnData: null,
    disablePadding: true,
    noData: false,
    show: true,
    sortable: false,
    filter: true,
    defaultFilter: true,
    custom: false,
  },
  {
    id: 'childRecipients',
    defaultWidth: '100px',
    type: 'multi',
    label: 'Family Members',
    align: 'center',
    columnData: null,
    noData: false,
    disablePadding: true,
    show: true,
    sortable: false,
    filter: false,
    defaultFilter: false,
    custom: false,
  },
  {
    id: 'parentRecipients',
    type: 'levels',
    label: 'Community',
    filterWidth: 150,
    align: 'center',
    noData: false,
    columnData: null,
    disablePadding: true,
    sortable: false,
    show: true,
    filter: true,
    noColumn: true,
    defaultFilter: true,
    custom: false,
  },
  {
    id: 'lastUpdate',
    defaultWidth: '180px',
    type: 'date',
    label: 'Last Update',
    filterWidth: 150,
    align: 'center',
    columnData: null,
    noData: true,
    disablePadding: true,
    show: true,
    sortable: true,
    filter: true,
    defaultFilter: true,
    custom: false,
  },
];

export const handleSelected = () => {};

export const dateRangeProps = {
  dateRange: { before: null, after: null },
  setDateRange: () => {},
};

export const lastUpdateProps = {
  lastUpdate: defaultState,
  setLastUpdate: () => {},
};

export const lastUpdatePropsCalendar = {
  lastUpdate: {
    query: 'custom',
    chips: [],
  },
  setLastUpdate: () => {},
};

const commonProps = {
  filterCategories: columns,
  dateRangeProps,
  lastUpdateProps: lastUpdatePropsCalendar,
};

const wrapperProps = {
  filterQueryTriggers: {
    completedSurveys: () => {},
    lastUpdate: () => {},
  },
  setSelected: () => {},
  messages: defineMessages({
    completedSurveys: {
      id: `app.test.completedSurveys`,
      defaultMessage: 'Completed Surveys',
    },
    lastUpdate: {
      id: `app.test.lastUpdate`,
      defaultMessage: 'Last Update',
    },
    parentRecipients: {
      id: `app.test.parentRecipients`,
      defaultMessage: 'Community',
    },
  }),
};

export const NSFiltersProviders = ({ children }) => (
  <NSFiltersProvider handleSelected={handleSelected} {...commonProps}>
    {children}
  </NSFiltersProvider>
);

export const NSFiltersProvidersCalendar = ({ children }) => (
  <NSFiltersProvider handleSelected={handleSelected} {...commonProps}>
    {children}
  </NSFiltersProvider>
);

export function NSFilterContainer() {

  return(
    <IntlProvider>
      <div>
        <h3>New Story Filters</h3>
        <NSFiltersProviders>
          <Wrapper {...commonProps} {...wrapperProps} />
        </NSFiltersProviders>
      </div>
    </IntlProvider>
  )
}
