import React from 'react'
import NSFilters from '../components/NSFilters'
import { defineMessages, IntlProvider } from 'react-intl';
import { NSFiltersProvider } from '../components/NSFilters/context/filters';
import Wrapper from '../components/NSFilters/index';
import { action } from '@storybook/addon-actions';
import MultiSelect from '../components/NSFilters/multi';

export default {
  title: 'NSFilters',
  component: NSFilters
}

const defaultState = { query: null, chips: [] };

const columns = [
  {
    id: 'recipientName',
    defaultWidth: '380px',
    label: 'Family',
    align: 'left',
    type: 'single',
    columnData: null,
    noData: false,
    disablePadding: true,
    show: true,
    sortable: true,
    defaultFilter: false,
    filter: false,
    custom: false,
  },
  {
    id: 'id',
    defaultWidth: '450px',
    label: 'Family ID',
    align: 'left',
    type: 'single',
    columnData: null,
    noData: false,
    disablePadding: false,
    show: true,
    sortable: true,
    filter: false,
    defaultFilter: false,
    custom: false,
  },
  {
    id: 'completedSurveys',
    defaultWidth: '123px',
    type: 'multi',
    label: 'Completed Surveys',
    filterWidth: 170,
    align: 'center',
    columnData: null,
    disablePadding: true,
    noData: false,
    show: true,
    sortable: false,
    filter: true,
    defaultFilter: true,
    custom: false,
  },
  {
    id: 'childRecipients',
    defaultWidth: '100px',
    type: 'multi',
    label: 'Family Members',
    align: 'center',
    columnData: null,
    noData: false,
    disablePadding: true,
    show: true,
    sortable: false,
    filter: false,
    defaultFilter: false,
    custom: false,
  },
  {
    id: 'parentRecipients',
    type: 'levels',
    label: 'Community',
    filterWidth: 150,
    align: 'center',
    noData: false,
    columnData: null,
    disablePadding: true,
    sortable: false,
    show: true,
    filter: true,
    noColumn: true,
    defaultFilter: true,
    custom: false,
  },
  {
    id: 'lastUpdate',
    defaultWidth: '180px',
    type: 'date',
    label: 'Last Update',
    filterWidth: 150,
    align: 'center',
    columnData: null,
    noData: true,
    disablePadding: true,
    show: true,
    sortable: true,
    filter: true,
    defaultFilter: true,
    custom: false,
  },
];

const data = [
  {
    id: '60786f62-5641-11e9-9966-22000bd4493b',
    name: 'Bolivia',
    toggled: true,
    checked: false,
    children: []
  },
  {
    id: '40786f62-3641-11e9-9916-22000bd4493b',
    name: 'Mexico',
    toggled: true,
    checked: false,
    children: [],
  },
];

const handleSelected = () => {};

const dateRangeProps = {
  dateRange: { before: null, after: null },
  setDateRange: () => {},
};

const lastUpdateProps = {
  lastUpdate: defaultState,
  setLastUpdate: () => {},
};

const lastUpdatePropsCalendar = {
  lastUpdate: {
    query: 'custom',
    chips: [],
  },
  setLastUpdate: () => {},
};

const commonProps = {
  filterCategories: columns,
  dateRangeProps,
  lastUpdateProps: lastUpdatePropsCalendar,
};

const wrapperProps = {
  filterQueryTriggers: {
    completedSurveys: () => {},
    lastUpdate: () => {},
  },
  setSelected: () => {},
  messages: defineMessages({
    completedSurveys: {
      id: `completedSurveys`,
      defaultMessage: 'Completed Surveys',
    },
    lastUpdate: {
      id: `lastUpdate`,
      defaultMessage: 'Last Update',
    },
    parentRecipients: {
      id: `parentRecipients`,
      defaultMessage: 'Community',
    },
  }),
};

const NSFiltersProviders = ({ children }) => (
  <NSFiltersProvider handleSelected={handleSelected} {...commonProps}>
    {children}
  </NSFiltersProvider>
);

export function NSFilterContainer() {

  return(
    <IntlProvider>
      <div onClick={action('Component handler')}>
        <h3>New Story Filters View</h3>
        <NSFiltersProviders>
          <Wrapper {...commonProps} {...wrapperProps} />
          <MultiSelect {...data} />
        </NSFiltersProviders>
      </div>
    </IntlProvider>
  )
}
